
public class ConcatinationOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String G1 = "Gopinath";
		String G2 = "Gayathri";
		System.out.println(G1+G2);
		int a=10;
		int b=20;
		System.out.println(a+b);
		System.out.println((G1+G2)+(a+b));
				
		
		
		
		
		
	}

}
